# summary(randomSlopes_m12)

#Error rates

randomSlopes_m1_err<-glmer(err_trials~1+val + (1+val|participant), 
                      data=raw.data, 
                      na.action = "na.omit", binomial)
# 
# randomSlopes_m1b_err<-glmer(err_trials~1+val + (1|participant), 
#                            data=raw.data, 
#                            na.action = "na.omit", binomial)

hist(raw.data$err_trials)

summary(randomSlopes_m1_err)
summary(randomSlopes_m1b_err)

anova(randomSlopes_m1b_err, randomSlopes_m1_err)
# significant interaction and significant main effect of saliency for both types of randomslopes 

plot_model(randomSlopes_m1_err, type = "pred", terms = c("sal", "val[minmax]"))

