#Will Interaction survive once we control for binding & retrieval effects?
#previous_response match, distance, and distance type as main effects
randomSlopes_m2<-lmer(RT_io~1+val + previous_rm_cwp+ Distance_cwp + Distance_type_cwp +(1+val + previous_rm_cwp + Distance_cwp + Distance_type_cwp|participant), 
                      data=raw.data, 
                      REML=F,
                      na.action = "na.omit")

summary(randomSlopes_m2)
#Main effect of responserelation and borderline significance of val:sal interaction at p = 0.055

randomSlopes_m2_err<-glmer(err_trials~1+val + previous_rm_cwp+ Distance_cwp + Distance_type_cwp +(1+val + previous_rm_cwp + Distance_cwp + Distance_type_cwp|participant), 
                           data=raw.data, 
                           binomial,
                           na.action = "na.omit")
#less complex model with only fixed effects and not random effects of factors
randomSlopes_m2b_err<-glmer(err_trials~1+val + previous_rm_cwp+ Distance_cwp + Distance_type_cwp +(1|participant), 
                            data=raw.data, 
                            binomial,
                            na.action = "na.omit")
tab_model(randomSlopes_m2,randomSlopes_m2_err, show.ci = FALSE, show.p = TRUE, show.stat = TRUE, show.aic = TRUE, show.loglik = TRUE)
summary(randomSlopes_m2_err)

anova(randomSlopes_m2b_err,randomSlopes_m2_err)

#highly significant val:sal interaction and main effect of saliency, PRevious response hasa p value of .09

#Model ohne Val and Sal, daf?r mit Interaktionen von Distance, previous_rm, und Distance type
randomSlopes_m3<-lmer(RT_io~1+ previous_rm_cwp*Distance_cwp*Distance_type_cwp +(1 + previous_rm_cwp*Distance_cwp*Distance_type_cwp|participant), 
                      data=raw.data, 
                      REML=F,
                      na.action = "na.omit")

summary(randomSlopes_m3)
#no interactions are significant but there is a main effect of response relation (t = 2.2)

randomSlopes_m3_err<-lmer(err_trials~1+ previous_rm_cwp*Distance_cwp*Distance_type_cwp +(1 + previous_rm_cwp*Distance_cwp*Distance_type_cwp|participant), 
                      data=raw.data, 
                      REML=F,
                      na.action = "na.omit")

summary(randomSlopes_m3_err)
#no significant effects but borderline for response rel (t = -1.6)


#RT:Einfaches SR binding hat "besten" effekt, aber nur knapp sign (t=1.954)
#Err: previous_rm x distance ia sign

#Interaktion Val*Sal wieder mit aufnehmen
randomSlopes_m4<-lmer(RT_io~1+val + previous_rm_cwp*Distance_cwp*Distance_type_cwp +(1+val + previous_rm_cwp*Distance_cwp*Distance_type_cwp|participant), 
                      data=raw.data, 
                      REML=F,
                      na.action = "na.omit")

summary(randomSlopes_m4)

randomSlopes_m4_err<-lmer(err_trials~1+val + previous_rm_cwp*Distance_cwp*Distance_type_cwp +(1+val + previous_rm_cwp*Distance_cwp*Distance_type_cwp|participant), 
                      data=raw.data, 
                      REML=F,
                      na.action = "na.omit")

summary(randomSlopes_m4_err)
#RT: val *sal Interaction borderline significant at p = 0.054, t value = 1.9 and PRevious response is significant at 0.03 with tvalue of 2.2
#Err: val *sal Interaction still significant, in addition, Saliency main effect and borderline validity t = 1.7

randomIntercept_m0<-lmer(RT_io~1 + (1|participant), 
                         data=raw.data, 
                         REML=F,
                         na.action = "na.omit")
summary(randomIntercept_m0)

randomIntercept_m0_err<-lmer(err_trials~1 + (1|participant), 
                         data=raw.data, 
                         REML=F,
                         na.action = "na.omit")
summary(randomIntercept_m0_err)


hist(raw.data$RT_io)
#Model comparison
#Model 3 vs Model0

anova(randomIntercept_m0, randomSlopes_m1)
anova(randomIntercept_m0_err, randomSlopes_m1_err)
#model 1 has sign better fit than model 0 for RT and Err

anova(randomSlopes_m1, randomSlopes_m2)
anova(randomSlopes_m1_err, randomSlopes_m2_err)
#model 2 has no  better fit than model 0 for RT and Err

anova(randomSlopes_m1, randomSlopes_m4)
#Model offers a better fit

randomSlopes_m5<-lmer(RT_io~1+val + previous_rm_cwp+ Distance_cwp + Distance_type_cwp+
                        previous_rm_mean+ Distance_mean + Distance_type_mean+
                        (1+val+sal + previous_rm_cwp + Distance_cwp + Distance_type_cwp|participant), 
                      data=raw.data, 
                      REML=F,
                      na.action = "na.omit")

anova(randomSlopes_m1, randomSlopes_m2, randomSlopes_m4,randomSlopes_m5)
summary(randomSlopes_m5) # significant val:sal interaction and a main effect of ResponseType
